import React, { useState, useMemo } from 'react';
import {
  Search,
  Menu,
  X,
  Users,
  ChevronRight,
  School,
  MapPin,
  Mail
} from 'lucide-react';

const ClubsWebsite = () => {
  const [selectedClub, setSelectedClub] = useState(null);
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState(null);

  const clubsData = [
    // your club data goes here
  ];

  const filteredClubs = useMemo(() => {
    if (!searchTerm.trim()) return clubsData;
    const lower = searchTerm.toLowerCase();
    return clubsData.filter(
      (club) =>
        club.name.toLowerCase().includes(lower) ||
        club.description.toLowerCase().includes(lower) ||
        club.category.toLowerCase().includes(lower) ||
        club.sponsor.toLowerCase().includes(lower)
    );
  }, [searchTerm, clubsData]);

  const categories = useMemo(() => {
    const cats = new Set(clubsData.map((c) => c.category));
    return Array.from(cats).sort();
  }, [clubsData]);

  const SearchBar = () => (
    <div className="p-6 border-b border-gray-200">
      <div className="relative">
        <Search
          size={20}
          className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400"
        />
        <input
          type="text"
          placeholder="Search by club name, sponsor, or category..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
        />
      </div>
    </div>
  );

  const CategoryGrid = () => (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
      {categories.map((cat) => (
        <button
          key={cat}
          onClick={() => setSelectedCategory(cat)}
          className="p-6 rounded-xl border-2 transition-all duration-200 hover:scale-105 hover:shadow-lg text-left bg-white"
        >
          <div className="flex items-center justify-between mb-3">
            <h3 className="text-lg font-semibold">{cat}</h3>
            <ChevronRight size={20} />
          </div>
          <p className="text-sm opacity-75 mb-3">
            View all {cat.toLowerCase()} clubs
          </p>
        </button>
      ))}
      {selectedCategory && (
        <button
          onClick={() => setSelectedCategory(null)}
          className="col-span-full text-left text-blue-600 hover:underline"
        >
          ← Show all categories
        </button>
      )}
    </div>
  );

  const ClubDetails = ({ club }) => (
    <div>
      <button
        onClick={() => setSelectedClub(null)}
        className="mb-6 flex items-center text-blue-600 hover:text-blue-800 transition-colors"
      >
        <ChevronRight size={20} className="rotate-180 mr-2" />
        Back to Clubs
      </button>
      <div className="bg-white rounded-xl shadow-lg border border-gray-200 overflow-hidden">
        <div className="p-8">
          <div className="flex items-start justify-between mb-6">
            <h1 className="text-3xl font-bold text-gray-900">{club.name}</h1>
            <span className="px-4 py-2 rounded-full text-sm font-medium bg-gray-100">
              {club.category}
            </span>
          </div>
          <div className="space-y-6">
            <div>
              <h2 className="text-lg font-semibold text-gray-900 mb-3">Description</h2>
              <p className="text-gray-700 leading-relaxed">{club.description}</p>
            </div>
            <div>
              <h2 className="text-lg font-semibold text-gray-900 mb-3">Sponsor</h2>
              <p className="text-gray-700">{club.sponsor}</p>
            </div>
            <div>
              <h2 className="text-lg font-semibold text-gray-900 mb-3">Category</h2>
              <span className="inline-block px-4 py-2 rounded-full text-sm font-medium bg-gray-100">
                {club.category}
              </span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-blue-50 to-indigo-100">
      <div className="bg-gradient-to-r from-blue-900 via-blue-800 to-indigo-900 text-white p-10">
        <h1 className="text-4xl font-bold">The Club Network @ WFHS</h1>
        <p className="text-blue-200 mt-2">Find and explore your next passion</p>
      </div>
      <div className="lg:hidden fixed top-4 left-4 z-50">
        <button
          onClick={() => setSidebarOpen((o) => !o)}
          className="p-2 bg-white rounded-lg shadow-lg border border-gray-200 hover:bg-gray-50 transition-colors"
        >
          {sidebarOpen ? <X size={24} /> : <Menu size={24} />}
        </button>
      </div>
      {sidebarOpen && (
        <div className="lg:hidden fixed inset-0 bg-black bg-opacity-50 z-40">
          <div className="bg-white w-64 h-full p-4 overflow-y-auto">
            <button onClick={() => setSidebarOpen(false)} className="mb-4 p-2">
              <X size={24} />
            </button>
            <SearchBar />
            <CategoryGrid />
          </div>
        </div>
      )}

      <div className="max-w-7xl mx-auto px-6 py-10">
        {!selectedClub ? (
          <>
            <SearchBar />
            <CategoryGrid />
          </>
        ) : (
          <ClubDetails club={filteredClubs.find((c) => c.id === selectedClub)} />
        )}
      </div>
    </div>
  );
};

export default ClubsWebsite;
