import React, { useState, useMemo } from 'react';
import { Search, Menu, X, Users, ChevronRight } from 'lucide-react';
import ClubCard from './components/club/ClubCard';
import ClubDetails from './components/club/ClubDetails';
import CategoryGrid from './components/club/CategoryGrid';
import SearchBar from './components/common/SearchBar';

const ClubsWebsite = () => {
  const [selectedClub, setSelectedClub] = useState('home');
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');

  // Club data parsed from CSV
  const clubsData = [
    // Add club data here
  ];

  const useClubFilter = (clubs, searchTerm = '', selectedCategory = null) => {
    // Group clubs by category
    const clubsByCategory = useMemo(() => {
      const grouped = clubs.reduce((acc, club) => {
        const category = club.category || 'Other';
        if (!acc[category]) {
          acc[category] = [];
        }
        acc[category].push(club);
        return acc;
      }, {});

      // Sort categories alphabetically and clubs within each category
      const sortedCategories = Object.keys(grouped).sort();
      const result = {};
      sortedCategories.forEach(category => {
        result[category] = grouped[category].sort((a, b) => a.name.localeCompare(b.name));
      });

      return result;
    }, [clubs]);

    // Filter clubs based on search term and category
    const filteredClubs = useMemo(() => {
      if (!searchTerm) return clubsByCategory;

      const filtered = {};
      Object.keys(clubsByCategory).forEach(category => {
        const categoryClubs = clubsByCategory[category].filter(club =>
          club.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
          club.description.toLowerCase().includes(searchTerm.toLowerCase())
        );
        if (categoryClubs.length > 0) {
          filtered[category] = categoryClubs;
        }
      });
      return filtered;
    }, [clubsByCategory, searchTerm]);

    return { clubsByCategory, filteredClubs };
  };

  const { clubsByCategory, filteredClubs } = useClubFilter(clubsData, searchTerm);
  const selectedClubData = clubsData.find(club => club.id === selectedClub);

  const CategoryColors = {
    'Academic': 'bg-blue-100 text-blue-800',
    'Arts': 'bg-purple-100 text-purple-800',
    'Business': 'bg-green-100 text-green-800',
    'Career': 'bg-orange-100 text-orange-800',
    'Cultural': 'bg-pink-100 text-pink-800',
    'Recreation': 'bg-yellow-100 text-yellow-800',
    'Religious': 'bg-indigo-100 text-indigo-800',
    'Service': 'bg-red-100 text-red-800',
    'Sports': 'bg-teal-100 text-teal-800',
    'STEM': 'bg-cyan-100 text-cyan-800',
    'Support': 'bg-gray-100 text-gray-800',
    'Leadership': 'bg-emerald-100 text-emerald-800',
    'Awareness': 'bg-violet-100 text-violet-800'
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-blue-50 to-indigo-100">
      {/* Mobile Menu Button */}
      <div className="lg:hidden fixed top-4 left-4 z-50">
        <button
          onClick={() => setSidebarOpen(!sidebarOpen)}
          className="p-2 bg-white rounded-lg shadow-lg border border-gray-200 hover:bg-gray-50 transition-colors"
        >
          {sidebarOpen ? <X size={24} /> : <Menu size={24} />}
        </button>
      </div>

      {/* Sidebar */}
      <div className={`fixed inset-y-0 left-0 z-40 w-80 bg-white shadow-2xl transform transition-transform duration-300 ease-in-out ${
        sidebarOpen ? 'translate-x-0' : '-translate-x-full'
      } lg:translate-x-0`}>
        {/* Header */}
        <div className="p-6 border-b border-gray-200 bg-gradient-to-r from-blue-600 to-indigo-600">
          <h1 className="text-xl font-bold text-white">WFHS Clubs</h1>
          <p className="text-blue-100 text-sm mt-1">& Organizations</p>
        </div>

        {/* Search */}
        <SearchBar searchTerm={searchTerm} setSearchTerm={setSearchTerm} />

        {/* Navigation */}
        <div className="px-6 py-4">
          <button
            onClick={() => setSelectedClub('home')}
            className={`w-full text-left p-3 rounded-lg mb-2 transition-colors ${
              selectedClub === 'home'
                ? 'bg-blue-100 text-blue-800 font-medium'
                : 'hover:bg-gray-100'
            }`}
          >
            <div className="flex items-center">
              <Users size={18} className="mr-3" />
              All Clubs
            </div>
          </button>

          {/* Categories */}
          <div className="mt-4">
            <h3 className="text-sm font-medium text-gray-500 uppercase tracking-wide mb-3">
              Categories
            </h3>
            {Object.keys(CategoryColors).map(category => (
              <button
                key={category}
                onClick={() => setSelectedClub(category)}
                className={`w-full text-left p-2 rounded-lg mb-1 transition-colors ${
                  selectedClub === category
                    ? 'bg-blue-100 text-blue-800 font-medium'
                    : 'hover:bg-gray-50'
                }`}
              >
                <div className="flex items-center justify-between">
                  <span>{category}</span>
                  <span className={`px-2 py-1 rounded-full text-xs ${CategoryColors[category]}`}>
                    {clubsByCategory[category]?.length || 0}
                  </span>
                </div>
              </button>
            ))}
          </div>
        </div>
      </div>

      {/* Overlay for mobile */}
      {sidebarOpen && (
        <div
          className="lg:hidden fixed inset-0 bg-black bg-opacity-50 z-30"
          onClick={() => setSidebarOpen(false)}
        />
      )}

      {/* Main content */}
      <div className="lg:ml-80 p-6">
        {selectedClub === 'home' ? (
          <div>
            <div className="mb-8">
              <h2 className="text-3xl font-bold text-gray-900 mb-2">
                Discover Your Community
              </h2>
              <p className="text-gray-600">
                Explore clubs and organizations at West Forsyth High School
              </p>
            </div>

            {/* Category Grid */}
            <CategoryGrid 
              categories={Object.keys(CategoryColors)}
              categoryColors={CategoryColors}
              clubsByCategory={clubsByCategory}
              onCategorySelect={setSelectedClub}
            />

            {/* All Clubs */}
            <div className="mt-12">
              <h3 className="text-2xl font-bold text-gray-900 mb-6">All Clubs</h3>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {clubsData.map(club => (
                  <ClubCard
                    key={club.id}
                    club={club}
                    categoryColors={CategoryColors}
                    onClick={() => setSelectedClub(club.id)}
                  />
                ))}
              </div>
            </div>
          </div>
        ) : selectedClubData ? (
          <ClubDetails
            club={selectedClubData}
            categoryColors={CategoryColors}
            onBack={() => setSelectedClub('home')}
          />
        ) : (
          <div>
            <div className="mb-8">
              <h2 className="text-3xl font-bold text-gray-900 mb-2">
                {selectedClub} Clubs
              </h2>
              <p className="text-gray-600">
                Clubs in the {selectedClub} category
              </p>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {filteredClubs[selectedClub]?.map(club => (
                <ClubCard
                  key={club.id}
                  club={club}
                  categoryColors={CategoryColors}
                  onClick={() => setSelectedClub(club.id)}
                />
              )) || (
                <div className="col-span-full text-center py-12">
                  <p className="text-gray-500">No clubs found in this category.</p>
                </div>
              )}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default ClubsWebsite;
