import React from 'react';
import { ChevronRight, User } from 'lucide-react';

const ClubCard = ({ club, onSelectClub, CategoryColors }) => {
  // Add this safety check to prevent the error
  if (!club) {
    return (
      <div className="bg-white rounded-xl p-6 shadow-lg border border-gray-100">
        <div className="animate-pulse">
          <div className="h-4 bg-gray-200 rounded w-3/4 mb-2"></div>
          <div className="h-3 bg-gray-200 rounded w-full mb-4"></div>
          <div className="h-3 bg-gray-200 rounded w-1/2"></div>
        </div>
      </div>
    );
  }

  const handleLearnMoreClick = (e) => {
    e.preventDefault();
    e.stopPropagation();
    if (onSelectClub && club.id) {
      console.log('Selecting club:', club.id); // Debug log
      onSelectClub(club.id);
    } else {
      // Fallback: show alert with club info if onSelectClub isn't working
      alert(`Learn more about ${club.name}!\n\nCategory: ${club.category}\nSponsor: ${club.sponsor}\n\nDescription: ${club.description}`);
    }
  };

  return (
    <div className="bg-white rounded-xl p-6 shadow-lg border border-gray-100 hover:shadow-xl transition-all duration-300 hover:-translate-y-1 flex flex-col h-full">
      {/* Header section - fixed height */}
      <div className="flex items-start justify-between mb-4">
        <h3 className="text-lg font-bold text-gray-900 flex-1 mr-2 leading-tight">
          {club.name || 'Unknown Club'}
        </h3>
        <span className={`px-3 py-1 rounded-full text-xs font-medium flex-shrink-0 ${CategoryColors?.[club.category] || 'bg-gray-100 text-gray-800'}`}>
          {club.category || 'Unknown'}
        </span>
      </div>

      {/* Description section - flexible height with consistent spacing */}
      <div className="flex-1 mb-4">
        <p className="text-gray-600 text-sm leading-relaxed">
          {club.description || 'No description available'}
        </p>
      </div>

      {/* Footer section - fixed height */}
      <div className="flex items-center justify-between mt-auto">
        <div className="flex items-center text-gray-500 text-sm flex-1 mr-4">
          <User size={14} className="mr-1 flex-shrink-0" />
          <span className="truncate">{club.sponsor || 'Unknown Sponsor'}</span>
        </div>

        <button
          onClick={handleLearnMoreClick}
          className="flex items-center text-blue-600 hover:text-blue-700 font-medium text-sm hover:bg-blue-50 px-3 py-2 rounded-md transition-colors flex-shrink-0"
        >
          Learn More
          <ChevronRight size={14} className="ml-1" />
        </button>
      </div>
    </div>
  );
};

// Demo component to show the ClubCard in action
const ClubCardDemo = () => {
  const sampleClub = {
    id: 1,
    name: "Robotics Club",
    category: "STEM",
    description: "Build and program robots while learning engineering principles and teamwork skills.",
    sponsor: "Ms. Johnson"
  };

  const CategoryColors = {
    'STEM': 'bg-blue-100 text-blue-800',
    'Arts': 'bg-purple-100 text-purple-800',
    'Sports': 'bg-green-100 text-green-800',
    'Academic': 'bg-orange-100 text-orange-800',
    'Service': 'bg-red-100 text-red-800'
  };

  const handleSelectClub = (clubId) => {
    console.log('Club selected:', clubId);
    alert(`You selected club ID: ${clubId}`);
  };

  return (
    <div className="p-8 bg-gray-50 min-h-screen">
      <h1 className="text-2xl font-bold mb-6">Club Card Demo</h1>
      <div className="max-w-sm">
        <ClubCard 
          club={sampleClub} 
          onSelectClub={handleSelectClub}
          CategoryColors={CategoryColors}
        />
      </div>
    </div>
  );
};

export default ClubCardDemo;
