import React, { useState, useMemo } from 'react';
import { Search, Menu, X, Users, ChevronRight } from 'lucide-react';
import ClubCard from './components/club/ClubCard';
import ClubDetails from './components/club/ClubDetails';
import CategoryGrid from './components/club/CategoryGrid';

const App = () => {
  const [selectedClub, setSelectedClub] = useState('home');
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');

  // Club data
  const clubsData = [
    {
      id: 'academic-bowl',
      name: 'Academic Bowl',
      description: 'A student group that practices & competes in core academic competitions',
      sponsor: 'J. Bush',
      category: 'Academic'
    },
    {
      id: 'anime-club',
      name: 'Anime Club',
      description: 'Do you love anime, manga, and all things Japanese?! Then this is the club for you! All are welcome whether you\'re new to the wonderful world of Anime or you are an expert!',
      sponsor: 'T. Porch',
      category: 'Cultural'
    },
    {
      id: 'art-club',
      name: 'Art Club',
      description: 'This club is a place for students interested in art to gather, create, and plan service projects in the community. Instagram @wfhs.nahs',
      sponsor: 'A. Henke, A. Holland',
      category: 'Arts'
    },
    {
      id: 'asian-culture-club',
      name: 'Asian Culture Club',
      description: 'An inclusive club for celebrating and further commemorating important aspects of a variety of asian cultures for people who are and are not of asian descent who want an opportunity to engage in fun, creative projects and eat delicious food.',
      sponsor: 'S. Carder',
      category: 'Cultural'
    },
    {
      id: 'beta-club',
      name: 'BETA Club',
      description: 'Promotes character, develops leadership skills & encourages service involvement Beta Club',
      sponsor: 'A. Carlisle, K. Ashe',
      category: 'Service'
    },
    {
      id: 'bingo-club',
      name: 'Bingo Club',
      description: 'To maintain wellness and balance in life through playing Bingo',
      sponsor: 'P. Valentino',
      category: 'Recreation'
    },
    {
      id: 'black-student-union',
      name: 'Black Student Union (BSU)',
      description: 'We are a social and service organization of students interested in developing leadership abilities in our school and community.',
      sponsor: 'S. Gooding, D. Barker, B. Lane',
      category: 'Cultural'
    },
    {
      id: 'cadence-connoisseurs',
      name: 'Cadence Connoisseurs - Music Theory Club',
      description: 'Music enthusiasts of all levels come together to explore music in a welcoming and engaging atmosphere.',
      sponsor: 'J. Hoffman, N. Tucker',
      category: 'Arts'
    },
    {
      id: 'chess-club',
      name: 'Chess Club',
      description: 'To play with new and experienced chess players for fun and competitions.',
      sponsor: 'P. Wilson',
      category: 'Recreation'
    },
    {
      id: 'computer-science-club',
      name: 'Computer Science Club',
      description: 'The Computer Science Club provides students in the West Forsyth community with the opportunity to code and collaborate with new people. We will acquire fresh information from each other and flourish as a community.',
      sponsor: 'P. Wilson',
      category: 'STEM'
    },
    {
      id: 'conditions-awareness-club',
      name: 'Conditions Awareness Club',
      description: 'To increase awareness of conditions that affect students at West. CAC gives students an opportunity to present conditions they may have, to learn about other conditions, and ultimately to feel a sense of belonging.',
      sponsor: 'J. Bush',
      category: 'Awareness'
    },
    {
      id: 'create-x',
      name: 'Create X',
      description: 'Inspire, educate, and empower students to explore entrepreneurship, creativity, and innovation.',
      sponsor: 'J. Martin',
      category: 'Business'
    },
    {
      id: 'creative-writing',
      name: 'Creative Writing',
      description: 'Students get together for games, write papers and give feedback together, and have parties.',
      sponsor: 'L. Smith',
      category: 'Arts'
    },
    {
      id: 'cricket-club',
      name: 'Cricket Club',
      description: 'Designed to express student\'s interest, passion, and desire to play, compete, and help represent our school by participating in regional tournaments across Georgia.',
      sponsor: 'E. Hardy',
      category: 'Sports'
    },
    {
      id: 'crocheting-for-cause',
      name: 'Crocheting for a Cause',
      description: 'To provide community service with handmade goods for those in need as well as educating and assisting beginning crocheters to further develop new techniques, problem solving skills, and patience.',
      sponsor: 'S. Ewing',
      category: 'Service'
    },
    {
      id: 'cti',
      name: 'CTI',
      description: 'Through West/Georgia CTI works with students with disabilities seeking to explore multiple career interests by offering leadership and work readiness conferences in various settings through groups and/or individually.',
      sponsor: 'K. Dankowsky',
      category: 'Career'
    },
    {
      id: 'data-science',
      name: 'Data Science',
      description: 'A club that builds programming and statistical skills – from scratch! We aim to benefit each other\'s understanding of the data and AI driven business world through fun, digestible collaborative activities.',
      sponsor: 'J. Hoffman',
      category: 'STEM'
    },
    {
      id: 'debate-team',
      name: 'Debate Team',
      description: 'Competitive Debate',
      sponsor: 'K. Troy',
      category: 'Academic'
    },
    {
      id: 'deca',
      name: 'DECA',
      description: 'DECA prepares emerging leaders and entrepreneurs in marketing, finance, hospitality and management in high school and colleges around the globe. Follow us on Instagram and Twitter to learn more! @westforsythDECA',
      sponsor: 'S. Boone Williams, L. Martin, J. Olson, C. Stemple',
      category: 'Business'
    }
    // Add more clubs as needed...
  ];

  // Group clubs by category
  const clubsByCategory = useMemo(() => {
    const grouped = clubsData.reduce((acc, club) => {
      if (!acc[club.category]) {
        acc[club.category] = [];
      }
      acc[club.category].push(club);
      return acc;
    }, {});

    // Sort categories and clubs within each category
    const sortedCategories = Object.keys(grouped).sort();
    const result = {};
    sortedCategories.forEach(category => {
      result[category] = grouped[category].sort((a, b) => a.name.localeCompare(b.name));
    });

    return result;
  }, []);

  // Filter clubs based on search term
  const filteredClubs = useMemo(() => {
    if (!searchTerm) return clubsByCategory;

    const filtered = {};
    Object.keys(clubsByCategory).forEach(category => {
      const categoryClubs = clubsByCategory[category].filter(club =>
        club.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        club.description.toLowerCase().includes(searchTerm.toLowerCase())
      );
      if (categoryClubs.length > 0) {
        filtered[category] = categoryClubs;
      }
    });
    return filtered;
  }, [clubsByCategory, searchTerm]);

  const selectedClubData = clubsData.find(club => club.id === selectedClub);

  const CategoryColors = {
    'Academic': 'bg-blue-100 text-blue-800',
    'Arts': 'bg-purple-100 text-purple-800',
    'Business': 'bg-green-100 text-green-800',
    'Career': 'bg-orange-100 text-orange-800',
    'Cultural': 'bg-pink-100 text-pink-800',
    'Recreation': 'bg-yellow-100 text-yellow-800',
    'Religious': 'bg-indigo-100 text-indigo-800',
    'Service': 'bg-red-100 text-red-800',
    'Sports': 'bg-teal-100 text-teal-800',
    'STEM': 'bg-cyan-100 text-cyan-800',
    'Support': 'bg-gray-100 text-gray-800',
    'Leadership': 'bg-emerald-100 text-emerald-800',
    'Awareness': 'bg-violet-100 text-violet-800'
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-blue-50 to-indigo-100">
      {/* Mobile Menu Button */}
      <div className="lg:hidden fixed top-4 left-4 z-50">
        <button
          onClick={() => setSidebarOpen(!sidebarOpen)}
          className="p-2 bg-white rounded-lg shadow-lg border border-gray-200 hover:bg-gray-50 transition-colors"
        >
          {sidebarOpen ? <X size={24} /> : <Menu size={24} />}
        </button>
      </div>

      {/* Sidebar */}
      <div className={`fixed inset-y-0 left-0 z-40 w-80 bg-white shadow-2xl transform transition-transform duration-300 ease-in-out ${
        sidebarOpen ? 'translate-x-0' : '-translate-x-full'
      } lg:translate-x-0 lg:static lg:inset-0`}>
        <div className="flex flex-col h-full">
          {/* Header */}
          <div className="p-6 border-b border-gray-200 bg-gradient-to-r from-blue-600 to-indigo-600">
            <h1 className="text-xl font-bold text-white">WFHS Clubs</h1>
            <p className="text-blue-100 text-sm mt-1">& Organizations</p>
          </div>

          {/* Search */}
          <div className="p-4 border-b border-gray-200">
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={18} />
              <input
                type="text"
                placeholder="Search clubs..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              />
            </div>
          </div>

          {/* Navigation */}
          <div className="flex-1 overflow-y-auto">
            <div className="p-4">
              <button
                onClick={() => {setSelectedClub('home'); setSidebarOpen(false);}}
                className={`w-full text-left p-3 rounded-lg mb-2 transition-colors flex items-center ${
                  selectedClub === 'home' ? 'bg-blue-100 text-blue-800 font-medium' : 'hover:bg-gray-100'
                }`}
              >
                <Users size={18} className="mr-3" />
                All Clubs Overview
              </button>

              {Object.keys(filteredClubs).map(category => (
                <div key={category} className="mb-4">
                  <h3 className="text-sm font-semibold text-gray-600 uppercase tracking-wide mb-2 px-3">
                    {category}
                  </h3>
                  {filteredClubs[category].map(club => (
                    <button
                      key={club.id}
                      onClick={() => {setSelectedClub(club.id); setSidebarOpen(false);}}
                      className={`w-full text-left p-3 rounded-lg mb-1 transition-colors text-sm ${
                        selectedClub === club.id ? 'bg-blue-100 text-blue-800 font-medium' : 'hover:bg-gray-50'
                      }`}
                    >
                      <div className="flex items-center justify-between">
                        <span className="truncate">{club.name}</span>
                        <ChevronRight size={14} className="flex-shrink-0 ml-2" />
                      </div>
                    </button>
                  ))}
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="lg:ml-80 min-h-screen">
        <div className="p-8 lg:p-12">
          {selectedClub === 'home' ? (
            <div>
              {/* Hero Section */}
              <div className="text-center mb-12">
                <h1 className="text-5xl lg:text-6xl font-bold bg-gradient-to-r from-blue-600 to-indigo-600 bg-clip-text text-transparent mb-4">
                  West Forsyth High School
                </h1>
                <h2 className="text-2xl lg:text-3xl font-semibold text-gray-700 mb-6">
                  Clubs & Organizations
                </h2>
                <p className="text-lg text-gray-600 max-w-3xl mx-auto">
                  Discover your passion and connect with like-minded students through our diverse range of clubs and organizations. 
                  From academic competitions to community service, there's something for everyone at WFHS.
                </p>
              </div>

              {/* Stats */}
              <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-12">
                <div className="bg-white rounded-2xl p-8 shadow-lg border border-gray-100 text-center">
                  <div className="text-4xl font-bold text-blue-600 mb-2">{clubsData.length}</div>
                  <div className="text-gray-600 font-medium">Total Clubs</div>
                </div>
                <div className="bg-white rounded-2xl p-8 shadow-lg border border-gray-100 text-center">
                  <div className="text-4xl font-bold text-indigo-600 mb-2">{Object.keys(clubsByCategory).length}</div>
                  <div className="text-gray-600 font-medium">Categories</div>
                </div>
                <div className="bg-white rounded-2xl p-8 shadow-lg border border-gray-100 text-center">
                  <div className="text-4xl font-bold text-purple-600 mb-2">100+</div>
                  <div className="text-gray-600 font-medium">Opportunities</div>
                </div>
              </div>

              {/* Categories Grid */}
              <CategoryGrid 
                clubsByCategory={clubsByCategory}
                CategoryColors={CategoryColors}
                onSelectClub={setSelectedClub}
              />
            </div>
          ) : selectedClubData ? (
            <ClubDetails 
              club={selectedClubData}
              CategoryColors={CategoryColors}
            />
          ) : (
            <div className="text-center py-12">
              <h2 className="text-2xl font-bold text-gray-900 mb-4">Club Not Found</h2>
              <p className="text-gray-600">The requested club could not be found.</p>
            </div>
          )}
        </div>
      </div>

      {/* Mobile Overlay */}
      {sidebarOpen && (
        <div 
          className="fixed inset-0 bg-black bg-opacity-50 z-30 lg:hidden"
          onClick={() => setSidebarOpen(false)}
        />
      )}
    </div>
  );
};

export default App;
