import React from 'react';
import { ChevronRight, User } from 'lucide-react';

const ClubCard = ({ club, onSelectClub, CategoryColors }) => {
  return (
    <div className="bg-white rounded-xl p-6 shadow-lg border border-gray-100 hover:shadow-xl transition-all duration-300 hover:-translate-y-1">
      <div className="flex items-start justify-between mb-4">
        <h3 className="text-lg font-bold text-gray-900 line-clamp-2">{club.name}</h3>
        <span className={`px-3 py-1 rounded-full text-xs font-medium flex-shrink-0 ml-2 ${CategoryColors[club.category] || 'bg-gray-100 text-gray-800'}`}>
          {club.category}
        </span>
      </div>

      <p className="text-gray-600 text-sm mb-4 line-clamp-3">
        {club.description}
      </p>

      <div className="flex items-center justify-between">
        <div className="flex items-center text-gray-500 text-sm">
          <User size={14} className="mr-1" />
          <span className="truncate">{club.sponsor}</span>
        </div>

        <button
          onClick={() => onSelectClub(club.id)}
          className="flex items-center text-blue-600 hover:text-blue-700 font-medium text-sm hover:bg-blue-50 px-3 py-1 rounded-md transition-colors"
        >
          Learn More
          <ChevronRight size={14} className="ml-1" />
        </button>
      </div>
    </div>
  );
};

export default ClubCard;
