import React, { useState, useMemo } from 'react';
import {
  Search,
  Menu,
  X,
  Users,
  ChevronRight,
  School,
  MapPin,
  Phone,
  Mail
} from 'lucide-react';

const ClubsWebsite = () => {
  const [selectedClub, setSelectedClub] = useState(null);
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState(null);

  // Club data parsed from CSV
  const clubsData = [ /* ... all club items ... */ ];

  const useClubFilter = (clubs, searchTerm = '', selectedCategory = null) => {
    const clubsByCategory = useMemo(() => {
      const grouped = clubs.reduce((acc, club) => {
        const category = club.category || 'Other';
        if (!acc[category]) acc[category] = [];
        acc[category].push(club);
        return acc;
      }, {});

      const sortedCategories = Object.keys(grouped).sort();
      const result = {};
      sortedCategories.forEach((cat) => {
        result[cat] = grouped[cat].sort((a, b) =>
          a.name.localeCompare(b.name)
        );
      });
      return result;
    }, [clubs]);

    const filteredByCategory = useMemo(() => {
      if (!selectedCategory) return clubsByCategory;
      return {
        [selectedCategory]: clubsByCategory[selectedCategory] || []
      };
    }, [clubsByCategory, selectedCategory]);

    const filteredClubs = useMemo(() => {
      const source = filteredByCategory;
      if (!searchTerm) return source;
      const lower = searchTerm.toLowerCase();
      return Object.fromEntries(
        Object.entries(source)
          .map(([cat, arr]) => [
            cat,
            arr.filter(
              (c) =>
                c.name.toLowerCase().includes(lower) ||
                c.description.toLowerCase().includes(lower)
            ),
          ])
          .filter(([, arr]) => arr.length)
      );
    }, [filteredByCategory, searchTerm]);

    return { clubsByCategory, filteredClubs };
  };

  const { clubsByCategory, filteredClubs } = useClubFilter(
    clubsData,
    searchTerm,
    selectedCategory
  );
  const selectedClubData = clubsData.find((c) => c.id === selectedClub);

  const CategoryColors = {
    Academic: 'bg-blue-100 text-blue-800 border-blue-200',
    Arts: 'bg-purple-100 text-purple-800 border-purple-200',
    Business: 'bg-green-100 text-green-800 border-green-200',
    Career: 'bg-orange-100 text-orange-800 border-orange-200',
    Cultural: 'bg-pink-100 text-pink-800 border-pink-200',
    Recreation: 'bg-yellow-100 text-yellow-800 border-yellow-200',
    Religious: 'bg-indigo-100 text-indigo-800 border-indigo-200',
    Service: 'bg-red-100 text-red-800 border-red-200',
    Sports: 'bg-teal-100 text-teal-800 border-teal-200',
    STEM: 'bg-cyan-100 text-cyan-800 border-cyan-200',
    Support: 'bg-gray-100 text-gray-800 border-gray-200',
    Leadership: 'bg-emerald-100 text-emerald-800 border-emerald-200'
  };

  const CategoryGrid = ({
    categories,
    categoryColors,
    clubsByCategory,
    onCategorySelect
  }) => (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
      {categories.map((cat) => (
        <button
          key={cat}
          onClick={() => onCategorySelect(cat)}
          className={`p-6 rounded-xl border-2 transition-all duration-200 hover:scale-105 hover:shadow-lg text-left ${categoryColors[cat]}`}
        >
          <div className="flex items-center justify-between mb-3">
            <h3 className="text-lg font-semibold">{cat}</h3>
            <ChevronRight size={20} />
          </div>
          <p className="text-sm opacity-75 mb-3">
            Explore {cat.toLowerCase()} clubs and organizations
          </p>
          <div className="flex items-center justify-between">
            <span className="text-2xl font-bold">
              {clubsByCategory[cat]?.length || 0}
            </span>
            <span className="text-xs font-medium">
              {clubsByCategory[cat]?.length === 1 ? 'Club' : 'Clubs'}
            </span>
          </div>
        </button>
      ))}
      {!!selectedCategory && (
        <button
          onClick={() => onCategorySelect(null)}
          className="col-span-full text-left text-blue-600 hover:underline"
        >
          ← Show All Categories
        </button>
      )}
    </div>
  );

  const SearchBar = ({ searchTerm, setSearchTerm }) => (
    <div className="p-6 border-b border-gray-200">
      <div className="relative">
        <Search
          size={20}
          className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400"
        />
        <input
          type="text"
          placeholder="Search clubs..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
        />
      </div>
    </div>
  );

  const ClubCard = ({ club }) => (
    <div
      onClick={() => setSelectedClub(club.id)}
      className="bg-white rounded-xl shadow-md hover:shadow-lg transition-shadow duration-200 cursor-pointer border border-gray-200"
    >
      <div className="p-6">
        <div className="flex items-start justify-between mb-4">
          <h3 className="text-lg font-semibold text-gray-900">{club.name}</h3>
          <span
            className={`px-3 py-1 rounded-full text-xs font-medium ${CategoryColors[club.category]}`}
          >
            {club.category}
          </span>
        </div>
        <p className="text-gray-600 text-sm mb-4 line-clamp-3">
          {club.description}
        </p>
        <div className="text-xs text-gray-500">
          <span className="font-medium">Sponsor:</span> {club.sponsor}
        </div>
      </div>
    </div>
  );

  const ClubDetails = ({ club }) => (
    <div>
      <button
        onClick={() => setSelectedClub(null)}
        className="mb-6 flex items-center text-blue-600 hover:text-blue-800 transition-colors"
      >
        <ChevronRight size={20} className="rotate-180 mr-2" />
        Back to Clubs
      </button>
      <div className="bg-white rounded-xl shadow-lg border border-gray-200 overflow-hidden">
        <div className="p-8">
          <div className="flex items-start justify-between mb-6">
            <h1 className="text-3xl font-bold text-gray-900">{club.name}</h1>
            <span
              className={`px-4 py-2 rounded-full text-sm font-medium ${CategoryColors[club.category]}`}
            >
              {club.category}
            </span>
          </div>

          <div className="space-y-6">
            <div>
              <h2 className="text-lg font-semibold text-gray-900 mb-3">
                Description
              </h2>
              <p className="text-gray-700 leading-relaxed">
                {club.description}
              </p>
            </div>
            <div>
              <h2 className="text-lg font-semibold text-gray-900 mb-3">
                Sponsor
              </h2>
              <p className="text-gray-700">{club.sponsor}</p>
            </div>
            <div>
              <h2 className="text-lg font-semibold text-gray-900 mb-3">
                Category
              </h2>
              <span
                className={`inline-block px-4 py-2 rounded-full text-sm font-medium ${CategoryColors[club.category]}`}
              >
                {club.category}
              </span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-blue-50 to-indigo-100">
      {/* Header */}
      <div className="bg-gradient-to-r from-blue-900 via-blue-800 to-indigo-900 text-white relative overflow-hidden">
        <div className="absolute inset-0 opacity-10">
          <div className="absolute inset-0 bg-gradient-to-r from-transparent via-white to-transparent transform rotate-12 scale-150" />
        </div>
        <div className="relative z-10 max-w-7xl mx-auto px-6 py-16">
          <div className="text-center mb-12">
            <div className="flex justify-center items-center mb-6">
              <School size={48} className="text-blue-200" />
            </div>
            <h1 className="text-5xl md:text-6xl font-bold mb-4 bg-gradient-to-r from-blue-200 to-white bg-clip-text text-transparent">
              West Forsyth High School
            </h1>
            <p className="text-xl md:text-2xl text-blue-200 font-light mb-2">
              Clubs & Organizations Directory
            </p>
            <p className="text-blue-300 text-lg">
              Discover your passion. Find your community. Make your mark.
            </p>
          </div>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-8 max-w-4xl mx-auto">
            <div className="text-center">
              <div className="bg-white/10 backdrop-blur-sm rounded-full w-16 h-16 flex items-center justify-center mx-auto mb-4">
                <Users size={32} className="text-blue-200" />
              </div>
              <h3 className="text-xl font-semibold mb-2">60+ Clubs</h3>
              <p className="text-blue-200 text-sm">
                Diverse organizations spanning academics, arts, service, and more
              </p>
            </div>
            <div className="text-center">
              <div className="bg-white/10 backdrop-blur-sm rounded-full w-16 h-16 flex items-center justify-center mx-auto mb-4">
                <MapPin size={32} className="text-blue-200" />
              </div>
              <h3 className="text-xl font-semibold mb-2">On Campus</h3>
              <p className="text-blue-200 text-sm">4155 Drew Rd, Cumming, GA 30041</p>
            </div>
            <div className="text-center">
              <div className="bg-white/10 backdrop-blur-sm rounded-full w-16 h-16 flex items-center justify-center mx-auto mb-4">
                <Mail size={32} className="text-blue-200" />
              </div>
              <h3 className="text-xl font-semibold mb-2">Get Involved</h3>
              <p className="text-blue-200 text-sm">
                Contact club sponsors to join and participate
              </p>
            </div>
          </div>
          <div className="text-center mt-12">
            <div className="inline-flex items-center px-6 py-3 bg-white/10 backdrop-blur-sm rounded-full text-blue-200 border border-white/20">
              <span className="text-sm font-medium">Academic Year 2024‑2025</span>
            </div>
          </div>
        </div>
      </div>

      {/* Mobile Sidebar Toggle */}
      <div className="lg:hidden fixed top-4 left-4 z-50">
        <button
          onClick={() => setSidebarOpen((o) => !o)}
          className="p-2 bg-white rounded-lg shadow-lg border border-gray-200 hover:bg-gray-50 transition-colors"
        >
          {sidebarOpen ? <X size={24} /> : <Menu size={24} />}
        </button>
      </div>
      {sidebarOpen && (
        <div className="lg:hidden fixed inset-0 bg-black bg-opacity-50 z-40">
          <div className="bg-white w-64 h-full p-4">
            <button
              onClick={() => setSidebarOpen(false)}
              className="mb-4 p-2"
            >
              <X size={24} />
            </button>
            <SearchBar searchTerm={searchTerm} setSearchTerm={setSearchTerm} />
            <CategoryGrid
              categories={Object.keys(clubsByCategory)}
              categoryColors={CategoryColors}
              clubsByCategory={clubsByCategory}
              onCategorySelect={(cat) => setSelectedCategory(cat)}
            />
          </div>
        </div>
      )}

      {/* Main Content */}
      <div className="max-w-7xl mx-auto px-6 py-10">
        {!selectedClubData ? (
          <>
            <SearchBar searchTerm={searchTerm} setSearchTerm={setSearchTerm} />
            <CategoryGrid
              categories={Object.keys(clubsByCategory)}
              categoryColors={CategoryColors}
              clubsByCategory={clubsByCategory}
              onCategorySelect={(cat) => setSelectedCategory(cat)}
            />
            <div className="mt-10 grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {Object.entries(filteredClubs).flatMap(([cat, arr]) =>
                arr.map((club) => (
                  <ClubCard key={club.id} club={club} />
                ))
              )}
            </div>
          </>
        ) : (
          <ClubDetails club={selectedClubData} />
        )}
      </div>
    </div>
  );
};

export default ClubsWebsite;
