import React, { useState, useMemo } from 'react';
import { Search, Menu, X, Users, ChevronRight } from 'lucide-react';

const ClubsWebsite = () => {
  const [selectedClub, setSelectedClub] = useState('home');
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');

  // Club data parsed from CSV
  const clubsData = [/* your club data remains unchanged */];

  const useClubFilter = (clubs, searchTerm = '', selectedCategory = null) => {
    const clubsByCategory = useMemo(() => {
      const grouped = clubs.reduce((acc, club) => {
        const category = club.category || 'Other';
        if (!acc[category]) {
          acc[category] = [];
        }
        acc[category].push(club);
        return acc;
      }, {});

      const sortedCategories = Object.keys(grouped).sort();
      const result = {};
      sortedCategories.forEach(category => {
        result[category] = grouped[category].sort((a, b) => a.name.localeCompare(b.name));
      });

      return result;
    }, [clubs]);

    const filteredClubs = useMemo(() => {
      if (!searchTerm) return clubsByCategory;

      const filtered = {};
      Object.keys(clubsByCategory).forEach(category => {
        const categoryClubs = clubsByCategory[category].filter(club =>
          club.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
          club.description.toLowerCase().includes(searchTerm.toLowerCase())
        );
        if (categoryClubs.length > 0) {
          filtered[category] = categoryClubs;
        }
      });
      return filtered;
    }, [clubsByCategory, searchTerm]);

    return { clubsByCategory, filteredClubs };
  };

  const { clubsByCategory, filteredClubs } = useClubFilter(clubsData, searchTerm);
  const selectedClubData = clubsData.find(club => club.id === selectedClub);

  const CategoryColors = {
    'Academic': 'bg-blue-100 text-blue-800 border-blue-200',
    'Arts': 'bg-purple-100 text-purple-800 border-purple-200',
    'Business': 'bg-green-100 text-green-800 border-green-200',
    'Career': 'bg-orange-100 text-orange-800 border-orange-200',
    'Cultural': 'bg-pink-100 text-pink-800 border-pink-200',
    'Recreation': 'bg-yellow-100 text-yellow-800 border-yellow-200',
    'Religious': 'bg-indigo-100 text-indigo-800 border-indigo-200',
    'Service': 'bg-red-100 text-red-800 border-red-200',
    'Sports': 'bg-teal-100 text-teal-800 border-teal-200',
    'STEM': 'bg-cyan-100 text-cyan-800 border-cyan-200',
    'Support': 'bg-gray-100 text-gray-800 border-gray-200',
    'Leadership': 'bg-emerald-100 text-emerald-800 border-emerald-200',  
  };

  const CategoryGrid = ({ categories, categoryColors, clubsByCategory, onCategorySelect }) => {
    return (
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
        {categories.map(category => (
          <button
            key={category}
            onClick={() => onCategorySelect(category)}
            className={`p-6 rounded-xl border-2 transition-all duration-200 hover:scale-105 hover:shadow-lg text-left ${categoryColors[category]}`}
          >
            <div className="flex items-center justify-between mb-3">
              <h3 className="text-lg font-semibold">{category}</h3>
              <ChevronRight size={20} />
            </div>
            <p className="text-sm opacity-75 mb-3">
              Explore {category.toLowerCase()} clubs and organizations
            </p>
            <div className="flex items-center justify-between">
              <span className="text-2xl font-bold">
                {clubsByCategory[category]?.length || 0}
              </span>
              <span className="text-xs font-medium">
                {clubsByCategory[category]?.length === 1 ? 'Club' : 'Clubs'}
              </span>
            </div>
          </button>
        ))}
      </div>
    );
  };

  const SearchBar = ({ searchTerm, setSearchTerm }) => {
    return (
      <div className="p-6 border-b border-gray-200">
        <div className="relative">
          <Search size={20} className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
          <input
            type="text"
            placeholder="Search clubs..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
          />
        </div>
      </div>
    );
  };

  const ClubCard = ({ club, categoryColors, onClick }) => {
    return (
      <div
        onClick={onClick}
        className="bg-white rounded-xl shadow-md hover:shadow-lg transition-shadow duration-200 cursor-pointer border border-gray-200"
      >
        <div className="p-6">
          <div className="flex items-start justify-between mb-4">
            <h3 className="text-lg font-semibold text-gray-900">{club.name}</h3>
            <span className={`px-3 py-1 rounded-full text-xs font-medium ${categoryColors[club.category]}`}>
              {club.category}
            </span>
          </div>
          <p className="text-gray-600 text-sm mb-4 line-clamp-3">
            {club.description}
          </p>
          <div className="text-xs text-gray-500">
            <span className="font-medium">Sponsor:</span> {club.sponsor}
          </div>
        </div>
      </div>
    );
  };

  const ClubDetails = ({ club, categoryColors, onBack }) => {
    return (
      <div>
        <button
          onClick={onBack}
          className="mb-6 flex items-center text-blue-600 hover:text-blue-800 transition-colors"
        >
          <ChevronRight size={20} className="rotate-180 mr-2" />
          Back to Clubs
        </button>

        <div className="bg-white rounded-xl shadow-lg border border-gray-200 overflow-hidden">
          <div className="p-8">
            <div className="flex items-start justify-between mb-6">
              <h1 className="text-3xl font-bold text-gray-900">{club.name}</h1>
              <span className={`px-4 py-2 rounded-full text-sm font-medium ${categoryColors[club.category]}`}>
                {club.category}
              </span>
            </div>

            <div className="space-y-6">
              <div>
                <h2 className="text-lg font-semibold text-gray-900 mb-3">Description</h2>
                <p className="text-gray-700 leading-relaxed">{club.description}</p>
              </div>

              <div>
                <h2 className="text-lg font-semibold text-gray-900 mb-3">Sponsor</h2>
                <p className="text-gray-700">{club.sponsor}</p>
              </div>

              <div>
                <h2 className="text-lg font-semibold text-gray-900 mb-3">Category</h2>
                <span className={`inline-block px-4 py-2 rounded-full text-sm font-medium ${categoryColors[club.category]}`}>
                  {club.category}
                </span>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-blue-50 to-indigo-100">
      {/* Topbar */}
      <div className="bg-gradient-to-r from-blue-900 via-blue-800 to-indigo-900 text-white px-6 py-4 lg:ml-80 shadow-sm border-b border-blue-700">
        <div className="max-w-7xl mx-auto">
          <h1 className="text-3xl font-bold">The Club Network @ WFHS</h1>
          <p className="text-blue-200 text-sm mt-1">Explore clubs and organizations at West Forsyth High School</p>
        </div>
      </div>

      {/* Mobile Menu Button */}
      <div className="lg:hidden fixed top-4 left-4 z-50">
        <button
          onClick={() => setSidebarOpen(!sidebarOpen)}
          className="p-2 bg-white rounded-lg shadow-lg border border-gray-200 hover:bg-gray-50 transition-colors"
        >
          {sidebarOpen ? <X size={24} /> : <Menu size={24} />}
        </button>
      </div>

      {/* Sidebar */}
      <div className={`fixed inset-y-0 left-0 z-40 w-80 bg-white shadow-2xl transform transition-transform duration-300 ease-in-out ${sidebarOpen ? 'translate-x-0' : '-translate-x-full'} lg:translate-x-0`}>
        <div className="p-6 border-b border-gray-200 bg-gradient-to-r from-blue-600 to-indigo-600">
          <h1 className="text-xl font-bold text-white">WFHS Clubs</h1>
          <p className="text-blue-100 text-sm mt-1">& Organizations</p>
        </div>
        <SearchBar searchTerm={searchTerm} setSearchTerm={setSearchTerm} />
        <div className="px-6 py-4">
          <button
            onClick={() => setSelectedClub('home')}
            className={`w-full text-left p-3 rounded-lg mb-2 transition-colors ${selectedClub === 'home' ? 'bg-blue-100 text-blue-800 font-medium' : 'hover:bg-gray-100'}`}
          >
            <div className="flex items-center">
              <Users size={18} className="mr-3" />
              All Clubs
            </div>
          </button>
          <div className="mt-4">
            <h3 className="text-sm font-medium text-gray-500 uppercase tracking-wide mb-3">Categories</h3>
            {Object.keys(CategoryColors).map(category => (
              <button
                key={category}
                onClick={() => setSelectedClub(category)}
                className={`w-full text-left p-2 rounded-lg mb-1 transition-colors ${selectedClub === category ? 'bg-blue-100 text-blue-800 font-medium' : 'hover:bg-gray-50'}`}
              >
                <div className="flex items-center justify-between">
                  <span>{category}</span>
                  <span className={`px-2 py-1 rounded-full text-xs bg-blue-100 text-blue-800`}>
                    {clubsByCategory[category]?.length || 0}
                  </span>
                </div>
              </button>
            ))}
          </div>
        </div>
      </div>

      {/* Overlay */}
      {sidebarOpen && (
        <div className="lg:hidden fixed inset-0 bg-black bg-opacity-50 z-30" onClick={() => setSidebarOpen(false)} />
      )}

      {/* Main Content */}
      <div className="lg:ml-80 p-6">
        {selectedClub === 'home' ? (
          <>
            <div className="mb-8">
              <h2 className="text-3xl font-bold text-gray-900 mb-2">Discover Your Community</h2>
              <p className="text-gray-600">Explore clubs and organizations at West Forsyth High School</p>
            </div>
            <CategoryGrid 
              categories={Object.keys(CategoryColors)}
              categoryColors={CategoryColors}
              clubsByCategory={clubsByCategory}
              onCategorySelect={setSelectedClub}
            />
          </>
        ) : selectedClubData ? (
          <ClubDetails
            club={selectedClubData}
            categoryColors={CategoryColors}
            onBack={() => setSelectedClub('home')}
          />
        ) : (
          <div>
            <div className="mb-8">
              <h2 className="text-3xl font-bold text-gray-900 mb-2">{selectedClub} Clubs</h2>
              <p className="text-gray-600">Clubs in the {selectedClub} category</p>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {filteredClubs[selectedClub]?.map(club => (
                <ClubCard
                  key={club.id}
                  club={club}
                  categoryColors={CategoryColors}
                  onClick={() => setSelectedClub(club.id)}
                />
              )) || (
                <div className="col-span-full text-center py-12">
                  <p className="text-gray-500">No clubs found in this category.</p>
                </div>
              )}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default ClubsWebsite;
